import React, { useState, useEffect } from 'react';
import { supabase } from '../../lib/supabaseClient';
import RegisterSale from '../RegisterSale/RegisterSale';
import '../../assets/css/ListSale.css';

const ListSale = () => {
  const [sales, setSales] = useState([]);
  const [filters, setFilters] = useState({});

  useEffect(() => {
    loadSales();
  }, [filters]);

  const loadSales = async () => {
    let query = supabase.from('sales').select('*').order('sale_time', { ascending: false });

    if (filters.dateFrom) {
      query = query.gte('sale_time', filters.dateFrom);
    }
    if (filters.dateTo) {
      query = query.lte('sale_time', filters.dateTo + 'T23:59:59');
    }
    if (filters.type) {
      query = query.eq('type', filters.type);
    }

    const { data, error } = await query;
    if (error) {
      console.error('Error al cargar las ventas:', error);
    } else {
      setSales(data);
    }
  };

  const applyFilters = () => {
    const dateFrom = document.getElementById('filter-date-from').value;
    const dateTo = document.getElementById('filter-date-to').value;
    const type = document.getElementById('filter-type').value;

    setFilters({
      dateFrom: dateFrom || null,
      dateTo: dateTo || null,
      type: type || null,
    });
  };

  const clearFilters = () => {
    document.getElementById('filter-date-from').value = '';
    document.getElementById('filter-date-to').value = '';
    document.getElementById('filter-type').value = '';
    setFilters({});
  };

  return (
    <div className="container">
      <div className="header text-center">
        <h1>Registro de Ventas</h1>
      </div>

      <div className="d-flex justify-content-between mb-3">
        <button className="btn btn-outline-secondary me-2" type="button" data-bs-toggle="collapse" data-bs-target="#filter-form" aria-expanded="false" aria-controls="filter-form">
          <i className="fas fa-filter"></i> Filtrar
        </button>
      </div>

      <div className="collapse" id="filter-form">
        <form className="mb-4">
          <div className="input-group mb-3">
            <label className="input-group-text" htmlFor="filter-date-from">Desde</label>
            <input type="date" className="form-control" id="filter-date-from" />
          </div>
          <div className="input-group mb-3">
            <label className="input-group-text" htmlFor="filter-date-to">Hasta</label>
            <input type="date" className="form-control" id="filter-date-to" />
          </div>
          <div className="input-group mb-3">
            <label className="input-group-text" htmlFor="filter-type">Tipo</label>
            <select id="filter-type" className="form-control">
              <option value="">Todos</option>
              <option value="service">Servicios</option>
              <option value="product">Productos</option>
            </select>
          </div>
          <button type="button" className="btn btn-primary w-100" onClick={applyFilters}>
            Aplicar Filtros
          </button>
          <button type="button" className="btn btn-outline-danger w-100 mt-2" onClick={clearFilters}>
            Eliminar Filtros
          </button>
        </form>
      </div>

      <RegisterSale onSale={loadSales} />

      <div id="salesList">
        {sales.length > 0 ? (
          sales.map((sale) => (
            <div key={sale.id} className="sale-item card mb-3 p-3">
              <div className="sale-details">
                <div className="sale-name">{sale.name}</div>
                <div className="sale-price">${sale.price}</div>
                <div className="sale-time text-muted">
                  {new Date(sale.sale_time).toLocaleDateString()}{' '}
                  {new Date(sale.sale_time).toLocaleTimeString()}
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-muted">No se encontraron ventas con los filtros aplicados.</p>
        )}
      </div>
    </div>
  );
};

export default ListSale;
