import React, { useState, useEffect } from 'react';
import { supabase } from '../lib/supabaseClient';
import '../assets/css/AdminInventory.css';

const AdminInventory = () => {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState('');
  const [type, setType] = useState('service');
  const [price, setPrice] = useState('');
  const [stock, setStock] = useState('');
  const [editingItemId, setEditingItemId] = useState(null);

  useEffect(() => {
    loadInventory();
  }, []);

  const loadInventory = async () => {
    const { data, error } = await supabase.from('inventory').select('*');
    if (error) {
      console.error('Error al cargar el inventario:', error);
    } else {
      setProducts(data);
    }
  };

  const handleTypeChange = (e) => setType(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const item = {
      name,
      type,
      price: parseFloat(price),
      stock: type === 'product' ? parseInt(stock) : null,
    };

    console.log('Item a insertar:', item);

    if (editingItemId) {
      const { error } = await supabase
        .from('inventory')
        .update(item)
        .eq('id', editingItemId);

      if (error) {
        console.error('Error al actualizar el item:', error);
      } else {
        resetForm();
        loadInventory();
      }
    } else {
      const { data, error } = await supabase
        .from('inventory')
        .insert([item]);

      if (error) {
        console.error('Error al insertar el item:', error);
      } else {
        console.log('Item insertado:', data);
        resetForm();
        loadInventory();
      }
    }
  };

  const editItem = async (id) => {
    const { data } = await supabase
      .from('inventory')
      .select('*')
      .eq('id', id)
      .single();

    if (data) {
      setEditingItemId(id);
      setName(data.name);
      setType(data.type);
      setPrice(data.price);
      if (data.type === 'product') setStock(data.stock);
    }
  };

  const deleteItem = async (id) => {
    const { error } = await supabase.from('inventory').delete().eq('id', id);
    if (!error) loadInventory();
  };

  const resetForm = () => {
    setName('');
    setType('service');
    setPrice('');
    setStock('');
    setEditingItemId(null);
  };

  return (
    <div className="container mt-5">
      <h1>Administraci√≥n de Inventario</h1>
      <form id="product-form" onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">Nombre</label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="type" className="form-label">Tipo</label>
          <select
            className="form-control"
            id="type"
            value={type}
            onChange={handleTypeChange}
            required
          >
            <option value="service">Servicio</option>
            <option value="product">Producto</option>
          </select>
        </div>
        <div className="mb-3">
          <label htmlFor="price" className="form-label">Precio</label>
          <input
            type="number"
            className="form-control"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
        </div>
        {type === 'product' && (
          <div className="mb-3" id="stock-group">
            <label htmlFor="stock" className="form-label">Stock</label>
            <input
              type="number"
              className="form-control"
              id="stock"
              value={stock}
              onChange={(e) => setStock(e.target.value)}
            />
          </div>
        )}
        <button type="submit" className="btn btn-primary">Guardar</button>
      </form>
      <h2 className="mt-5">Inventario</h2>
      <ul id="products-list" className="list-group">
        {products.map((item) => (
          <li key={item.id} className="list-group-item">
            {item.name} - {item.type} - ${item.price} {item.type === 'product' ? '- Stock: ' + item.stock : ''}
            <div className="actions">
              <button className="btn btn-secondary btn-sm" onClick={() => editItem(item.id)}>Editar</button>
              <button className="btn btn-danger btn-sm" onClick={() => deleteItem(item.id)}>Eliminar</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminInventory;
